(565, 15, '2017-04-03 18:53:10', '2017-04-03 18:53:10', '[spb_row wrap_type=\"content-width\" parallax_image_height=\"content-height\" parallax_image_movement=\"fixed\" parallax_image_speed=\"0.5\" parallax_video_height=\"window-height\" parallax_video_overlay=\"none\" row_overlay_opacity=\"0\" width=\"1/1\" el_position=\"first last\"] [spb_blank_spacer height=\"30\" width=\"1/1\" el_position=\"first last\"] [spb_text_block title=\"Case Study Series\" animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/1\" el_position=\"first last\"]\r\n<p class=\"p1\"><span class=\"s1\">This series of three case studies details the lessons learned throughout the development process and best practices for creating online school report card tools. Each case study focuses on a different topic area, and includes an action checklist on the last page to help states in their improvement efforts. </span></p>\r\n[/spb_text_block] [spb_text_block animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/3\" el_position=\"first\"]\r\n\r\n<a href=\"http://localhost:8888/wp-content/uploads/2017/03/EiE_CaseStudy1_v2_links.pdf\" target=\"_blank\"><img class=\"alignnone wp-image-553 size-medium aligncenter\" src=\"http://localhost:8888/wp-content/uploads/2017/03/case-Study-1-cover-232x300.png\" alt=\"\" width=\"232\" height=\"300\" /></a>\r\n<p style=\"text-align: center;\"><a href=\"http://localhost:8888/wp-content/uploads/2017/03/EiE_CaseStudy1_v2_links.pdf\" target=\"_blank\">Developing Online\r\nSchool Report Cards</a> (PDF)</p>\r\n[/spb_text_block] [spb_text_block animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/3\"]\r\n\r\n<a href=\"http://localhost:8888/wp-content/uploads/2017/03/EiE_CaseStudy2_v6.pdf\" target=\"_blank\"><img class=\"alignnone size-medium wp-image-555 aligncenter\" src=\"http://localhost:8888/wp-content/uploads/2017/03/Case-Study-2-cover-232x300.png\" alt=\"\" width=\"232\" height=\"300\" /></a>\r\n<p style=\"text-align: center;\"><a href=\"http://localhost:8888/wp-content/uploads/2017/03/EiE_CaseStudy2_v6.pdf\" target=\"_blank\">Developing Online\r\nSchool Report Cards</a> (PDF)</p>\r\n[/spb_text_block] [spb_text_block animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/3\" el_position=\"last\"]\r\n<p style=\"text-align: center;\">Case Study 3 (Coming Soon)</p>\r\n[/spb_text_block] [/spb_row] [spb_row wrap_type=\"content-width\" parallax_image_height=\"content-height\" parallax_image_movement=\"fixed\" parallax_image_speed=\"0.5\" parallax_video_height=\"window-height\" parallax_video_overlay=\"none\" row_overlay_opacity=\"0\" width=\"1/1\" el_position=\"first last\"] [spb_text_block title=\"Development to Launch Timeline\" animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/1\" el_position=\"first last\"]\r\n<p class=\"p1\"><span class=\"s1\">This document, created in collaboration with the <a href=\"http://www.ccsso.org/\"><span class=\"s2\">Council of Chief State School Officers</span></a>, <a href=\"http://collaborativecommunications.com/\"><span class=\"s2\">Collaborative Communications Group</span></a>, <a href=\"http://dataqualitycampaign.org/\"><span class=\"s2\">Data Quality Campaign</span></a> and <a href=\"http://excelined.org/\"><span class=\"s2\">ExcelinEd</span></a> is a 12-month development to launch timeline and project plan for states looking to create their own next-generation school accountability reporting tool. </span></p>\r\n<p class=\"p1\"><a href=\"http://localhost:8888/wp-content/uploads/2017/03/TimelineDesign_v5.pdf\" target=\"_blank\"><img class=\"alignnone size-medium wp-image-557\" src=\"http://localhost:8888/wp-content/uploads/2017/03/Timeline_cover-300x232.png\" alt=\"\" width=\"300\" height=\"232\" /></a></p>\r\n<p class=\"p1\"><a href=\"http://localhost:8888/wp-content/uploads/2017/03/TimelineDesign_v5.pdf\" target=\"_blank\">Development to Launch Timeline</a> (PDF)</p>\r\n[/spb_text_block] [/spb_row] [spb_row wrap_type=\"content-width\" parallax_image_height=\"content-height\" parallax_image_movement=\"fixed\" parallax_image_speed=\"0.5\" parallax_video_height=\"window-height\" parallax_video_overlay=\"none\" row_overlay_opacity=\"0\" width=\"1/1\" el_position=\"first last\"] [spb_text_block title=\"Open Source Code\" animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/1\" el_position=\"first last\"]\r\n<p class=\"p1\"><span class=\"s1\">This link leads to the Github repository housing the code that powers the tool. This code is available for free as a resource for developers working to help other states advance their efforts to build online school report card tools.</span></p>\r\n<p class=\"p1\"><span class=\"s1\">[Link to Github repository with code]</span></p>\r\n[/spb_text_block] [/spb_row] [spb_row wrap_type=\"content-width\" parallax_image_height=\"content-height\" parallax_image_movement=\"fixed\" parallax_image_speed=\"0.5\" parallax_video_height=\"window-height\" parallax_video_overlay=\"none\" row_overlay_opacity=\"0\" width=\"1/1\" el_position=\"first last\"] [spb_text_block title=\"Learn More\" animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/1\" el_position=\"first last\"][/spb_text_block] [spb_text_block animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/3\" el_position=\"first\"]\r\n<p class=\"p1\"><img class=\"alignnone size-medium wp-image-559 aligncenter\" src=\"http://localhost:8888/wp-content/uploads/2017/03/Talking-Points-for-Foundation-Staff_web-232x300.png\" alt=\"\" width=\"232\" height=\"300\" /></p>\r\n<p class=\"p1\" style=\"text-align: center;\"><a href=\"http://localhost:8888/wp-content/uploads/2017/03/Talking-Points-for-Foundation-Staff_web.pdf\" target=\"_blank\">Talking Points</a> (PDF)</p>\r\n[/spb_text_block] [spb_text_block animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/3\"]\r\n\r\nFAQ\r\n\r\n[/spb_text_block] [spb_blank_spacer height=\"30\" width=\"1/3\" el_position=\"last\"] [spb_text_block animation=\"none\" animation_delay=\"0\" padding_vertical=\"0\" padding_horizontal=\"0\" width=\"1/1\" el_position=\"first last\"]\r\n<p class=\"p1\"><span class=\"s1\">Contact <a href=\"mailto:KnowYourSchoolProject@ExcelinEd.org\"><span class=\"s2\">KnowYourSchoolProject@ExcelinEd.org</span></a> to learn more about online report cards in your state.</span></p>\r\n[/spb_text_block] [/spb_row]', 'Resources for SEAs', '', 'inherit', 'closed', 'closed', '', '550-autosave-v1', '', '', '2017-04-03 18:53:10', '2017-04-03 18:53:10', '', 550, 'http://localhost:8888/550-autosave-v1/', 0, 'revision', '', 0);
